import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../context/AuthContext';

const AdminWrapper = styled.div`
  min-height: 100vh;
  padding: 6rem 1rem 3rem;
  background: linear-gradient(135deg, rgba(255,127,169,0.1), rgba(255,226,236,0.3));
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
`;

const Header = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  animation: fadeInDown 0.6s ease;

  h1 {
    font-size: 3rem;
    font-weight: 800;
    background: var(--gradient-pink);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin-bottom: 0.5rem;
  }

  p {
    color: var(--gray);
    font-size: 1.1rem;
  }
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin-bottom: 3rem;
`;

const StatCard = styled.div`
  background: var(--white);
  padding: 2rem;
  border-radius: 20px;
  box-shadow: var(--shadow-3d);
  display: flex;
  align-items: center;
  gap: 1.5rem;
  transition: all var(--transition-normal);
  animation: scaleIn 0.5s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 40px rgba(255, 105, 180, 0.3);
  }
`;

const StatIcon = styled.div`
  width: 70px;
  height: 70px;
  border-radius: 15px;
  background: ${props => props.$gradient || 'var(--gradient-pink)'};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2rem;
  color: var(--white);
  box-shadow: var(--shadow-pink);
`;

const StatInfo = styled.div`
  flex: 1;

  h3 {
    font-size: 2rem;
    font-weight: 700;
    color: var(--primary-black);
    margin-bottom: 0.25rem;
  }

  p {
    color: var(--gray);
    font-size: 0.95rem;
  }
`;

const DashboardGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;

  @media (max-width: 968px) {
    grid-template-columns: 1fr;
  }
`;

const Card = styled.div`
  background: var(--white);
  border-radius: 20px;
  padding: 2rem;
  box-shadow: var(--shadow-3d);
  animation: fadeIn 0.6s ease;

  h2 {
    font-size: 1.5rem;
    color: var(--primary-black);
    margin-bottom: 1.5rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;

    i {
      color: var(--primary-pink);
    }
  }
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;

  thead {
    background: var(--off-white);
    
    th {
      padding: 1rem;
      text-align: left;
      font-weight: 600;
      color: var(--primary-black);
      font-size: 0.9rem;
      border-radius: 8px;
    }
  }

  tbody {
    tr {
      border-bottom: 1px solid var(--off-white);
      transition: background var(--transition-fast);

      &:hover {
        background: rgba(255, 105, 180, 0.05);
      }

      &:last-child {
        border-bottom: none;
      }
    }

    td {
      padding: 1rem;
      color: var(--primary-black);
      font-size: 0.9rem;
    }
  }
`;

const Badge = styled.span`
  padding: 0.4rem 0.8rem;
  border-radius: 20px;
  font-size: 0.8rem;
  font-weight: 600;
  display: inline-block;

  &.pending {
    background: #fff3cd;
    color: #856404;
  }

  &.processing {
    background: #cfe2ff;
    color: #084298;
  }

  &.shipped {
    background: #d1e7dd;
    color: #0f5132;
  }

  &.delivered {
    background: #d1e7dd;
    color: #0f5132;
  }
`;

const ActionButton = styled.button`
  padding: 0.5rem 1rem;
  border-radius: 8px;
  font-size: 0.85rem;
  font-weight: 600;
  cursor: pointer;
  transition: all var(--transition-fast);
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  border: none;
  background: var(--gradient-pink);
  color: var(--white);

  &:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-pink);
  }

  &.secondary {
    background: var(--off-white);
    color: var(--primary-black);

    &:hover {
      background: var(--primary-pink);
      color: var(--white);
    }
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem 1rem;
  color: var(--gray);

  i {
    font-size: 4rem;
    margin-bottom: 1rem;
    opacity: 0.3;
  }

  p {
    font-size: 1.1rem;
  }
`;

const AdminDashboard = () => {
  const navigate = useNavigate();
  const { currentUser, isAuthenticated, isAdmin } = useAuth();
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    if (!isAuthenticated || !isAdmin) {
      navigate('/');
      return;
    }

    // Load orders from localStorage
    const savedOrders = JSON.parse(localStorage.getItem('zoiesOrders') || '[]');
    setOrders(savedOrders.slice(0, 5)); // Show last 5 orders

    // Load users from localStorage
    const savedUsers = JSON.parse(localStorage.getItem('zoiesUsers') || '[]');
    setUsers(savedUsers);
  }, [isAuthenticated, isAdmin, navigate]);

  const stats = [
    {
      icon: 'fas fa-shopping-bag',
      title: orders.length,
      label: 'Total Orders',
      gradient: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
    },
    {
      icon: 'fas fa-users',
      title: users.length,
      label: 'Total Users',
      gradient: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)'
    },
    {
      icon: 'fas fa-box',
      title: '8',
      label: 'Products',
      gradient: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'
    },
    {
      icon: 'fas fa-dollar-sign',
      title: `$${orders.reduce((sum, order) => sum + (order.total || 0), 0).toFixed(2)}`,
      label: 'Total Revenue',
      gradient: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)'
    }
  ];

  if (!isAuthenticated || !isAdmin) {
    return null;
  }

  return (
    <AdminWrapper>
      <Container>
        <Header>
          <h1>Admin Dashboard</h1>
          <p>Welcome back, {currentUser?.username}!</p>
        </Header>

        <StatsGrid>
          {stats.map((stat, index) => (
            <StatCard key={index}>
              <StatIcon $gradient={stat.gradient}>
                <i className={stat.icon}></i>
              </StatIcon>
              <StatInfo>
                <h3>{stat.title}</h3>
                <p>{stat.label}</p>
              </StatInfo>
            </StatCard>
          ))}
        </StatsGrid>

        <DashboardGrid>
          <Card>
            <h2>
              <i className="fas fa-shopping-bag"></i>
              Recent Orders
            </h2>
            {orders.length > 0 ? (
              <Table>
                <thead>
                  <tr>
                    <th>Order ID</th>
                    <th>Customer</th>
                    <th>Total</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {orders.map((order, index) => (
                    <tr key={index}>
                      <td>#{order.id || index + 1}</td>
                      <td>{order.name || 'N/A'}</td>
                      <td>${order.total?.toFixed(2) || '0.00'}</td>
                      <td>
                        <Badge className={order.status || 'pending'}>
                          {order.status || 'Pending'}
                        </Badge>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            ) : (
              <EmptyState>
                <i className="fas fa-shopping-bag"></i>
                <p>No orders yet</p>
              </EmptyState>
            )}
          </Card>

          <Card>
            <h2>
              <i className="fas fa-users"></i>
              Registered Users
            </h2>
            {users.length > 0 ? (
              <Table>
                <thead>
                  <tr>
                    <th>Username</th>
                    <th>Email</th>
                    <th>Role</th>
                  </tr>
                </thead>
                <tbody>
                  {users.slice(0, 5).map((user, index) => (
                    <tr key={index}>
                      <td>{user.username}</td>
                      <td>{user.email}</td>
                      <td>
                        <Badge className={user.role === 'admin' ? 'processing' : 'pending'}>
                          {user.role}
                        </Badge>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            ) : (
              <EmptyState>
                <i className="fas fa-users"></i>
                <p>No registered users yet</p>
              </EmptyState>
            )}
          </Card>
        </DashboardGrid>

        <Card style={{ marginTop: '2rem' }}>
          <h2>
            <i className="fas fa-cog"></i>
            Quick Actions
          </h2>
          <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
            <ActionButton>
              <i className="fas fa-plus"></i>
              Add Product
            </ActionButton>
            <ActionButton className="secondary">
              <i className="fas fa-file-invoice"></i>
              View All Orders
            </ActionButton>
            <ActionButton className="secondary">
              <i className="fas fa-users-cog"></i>
              Manage Users
            </ActionButton>
            <ActionButton className="secondary">
              <i className="fas fa-chart-line"></i>
              View Analytics
            </ActionButton>
          </div>
        </Card>
      </Container>
    </AdminWrapper>
  );
};

export default AdminDashboard;
